import React, { useState } from 'react';
import { getProductType } from './ProductTypeData/data'
import { productCon, productSubTypeCon, productTypeCon } from '../context/CollectionsName/names'
export const ProductContext = React.createContext();
export const ProductProvider = (props) => {
    const productModel = {
        id: "1",
        link: "",
        name: "",
        typeId: "1",
        subTypeId: "",
    };
    const errorModel =
    {
        id: { show: false },
        name: { show: false },
        typeId: { show: false },
        subTypeId: { show: false },
    }

    const [product, setType] = useState(productModel);
    const [products, setTypes] = useState(null);
    const [productTypes, setProductTypes] = useState([]);
    const [productSubTypes, setProductSubTypes] = useState([]);
    const [updateAction, setUpdateAction] = useState(false);
    const [errorMsg, setErrorMsg] = useState(errorModel);
    const [event, setEvent] = useState({
        loading: true,
        error: false
    });

    const getProducts = async () => {
        productCon.get().then((collections) => {
            const datas = collections.docs.map((product) => product.data());
            setTableLoading(datas, false, false);
            console.log("datas" + datas.length);
        }).catch((ex) => {
            console.log("error" + ex);
            setTableLoading([], false, false);
        });

    }
    const getProductTypes = async () => {
        productTypeCon.get().then((collections) => {
            const datas = collections.docs.map((product) => product.data());
            productTypes = datas;
            console.log("datas" + datas.length);
        }).catch((ex) => {
            console.log("error" + ex);
            setTableLoading([], false, false);
        });
    }

    const getProductSubTypes = async () => {
        productSubTypeCon.get().then((collections) => {
            const datas = collections.docs.map((product) => product.data());
            productSubTypeCon = datas;
        }).catch((ex) => {
            console.log("error" + ex);

        });
    }
    const [table, setTable] = useState({
        loading: true,
        error: false
    });
    const [formValid, setFormValid] = useState(false);




    const addProduct = () => {
        setTableLoading(products, true, false);
        productCon.doc(product.id).set(product).then((collections) => {
            setType({ id: "", name: "" });
            setUpdateAction(false);
            alert(updateAction ? "Ажилттай заслаа" : "Ажилттай нэмлээ");

        }).catch((ex) => {
            console.log("error" + ex);
            setTableLoading(products, false, false);
            alert(updateAction ? "Засхад алдаа гарлаа " : "Нэмхэд алдаа гарлаа ");
        });

    }

    const setEventLoading = (product, isLoading, error) => {
        event.loading = isLoading;
        event.error = error;
        setType(product);
        setEvent(event);
        setType({
            id: "",
            name: ""
        });
        setFormValid(false);
    }

    const setTableLoading = (products, isLoading, error) => {
        table.loading = isLoading;
        table.error = error;
        setTable(table);
        setTypes(products);
    }



    return <ProductContext.Provider value={{ errorModel, productModel, updateAction, products, table, product, productTypes, productSubTypes, errorMsg, formValid, event, setErrorMsg, setType, setFormValid, addProduct, setUpdateAction, getProducts }}>{props.children}</ProductContext.Provider>
}
