import React from 'react'
import TypeTable from '../dataTable/ProductTypeTable'
import ProductTypeAdd from '../components/ProductTypeAdd'
import { ProductTypeContext } from '../../context/ProductTypeCtx'
import { validation, validatorTypes } from '../../util/validator'
const ProductTypeContent = () => {
    const ctx = React.useContext(ProductTypeContext);
    const handleInputChange = (event) => {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        const validationTypes = {
            name: validatorTypes.POSITIVENUMBER,
            id: validatorTypes.POSITIVENUMBER,
        };
        const validate = validation(
            value,
            validationTypes[name],
            name === "id" && ctx.type
        );
        let formValid = false;
        const type = {
            ...ctx.type,
            [name]: value,
        };
        if (!validate.show) {
            let istemvavlid = true;
            Object.values(type).map((el, index) => {
                if (
                    el.length === 0 &&
                    Object.keys(ctx.type)[index] !== [name]
                ) {
                    istemvavlid = false;
                }
            });
            if (istemvavlid) {
                formValid = true;
            }
        }
        const errorMsg = {
            ...ctx.errorMsg,
            [name]: validate,
        }


        ctx.setType(type);
        ctx.setErrorMsg(errorMsg);
        ctx.setFormValid(formValid);
    }

    return (

        <div className="content-wrapper">
            <div className="content-header">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-4 rounded-top" >
                            <ProductTypeAdd formValid={ctx.formValid} errorMsg={ctx.errorMsg} type={ctx.type} handleInputChange={handleInputChange} addProductType={ctx.addProductType}></ProductTypeAdd>
                        </div>
                        {ctx.table.loading ? <div>loading </div> :
                            <div className="col-8"><TypeTable types={ctx.types}></TypeTable></div>
                        }
                    </div>
                </div>
            </div>


        </div >
    )
}

export default ProductTypeContent;
