import React, { useState } from 'react';
import firebase from '../../firebase'
const productTypeCon = firebase.firestore().collection('productType');
export const ProductTypeContext = React.createContext();
export const ProductTypeProvider = (props) => {
    const [type, setType] = useState(null);
    const [types, setTypes] = useState(null);

    const [updateAction, setUpdateAction] = useState(false);
    const [errorMsg, setErrorMsg] = useState({
        id: { show: false },
        name: { show: false }
    });
    const [event, setEvent] = useState({
        loading: true,
        error: false
    });

    const [table, setTable] = useState({
        loading: true,
        error: false
    });
    const [formValid, setFormValid] = useState(false);

    React.useEffect(() => {
        getProductType();
    }, []);

    const getProductType = () => {
        productTypeCon.get().then((collections) => {
            const datas = collections.docs.map((type) => type.data());
            setTableLoading(datas, false, false);
            console.log("datas" + datas.length);
        }).catch((ex) => {
            console.log("error" + ex);
            setTableLoading([], false, false);
        });
    }

    const addProductType = () => {
        setTableLoading(types, true, false);
        productTypeCon.doc(type.id).set(type).then((collections) => {
            getProductType();
            setType({ id: "", name: "" });
            setUpdateAction(false);
            alert("Ажилттай нэмлээ");

        }).catch((ex) => {
            console.log("error" + ex);
            setTableLoading(types, false, false);
            alert("Нэмхэд алдаа гарлаа ");
        });

    }

    const setEventLoading = (type, isLoading, error) => {
        event.loading = isLoading;
        event.error = error;
        setType(type);
        setEvent(event);
        setType({
            id: "",
            name: ""
        });
        setFormValid(false);
    }

    const setTableLoading = (types, isLoading, error) => {
        table.loading = isLoading;
        table.error = error;
        setTable(table);
        setTypes(types);
    }
    const databaseUpdate = (type) => {
        productTypeCon.doc(String(type.id)).set().then((res) => {
            alert("amjilttal nemlee");

        }).catch((ex) => console.log("aldaa garlaa") + ex).finally(ex => {
            getProductType();
        });
    }


    return <ProductTypeContext.Provider value={{ updateAction, types, table, type, errorMsg, formValid, event, setErrorMsg, setType, setFormValid, addProductType, setUpdateAction }}>{props.children}</ProductTypeContext.Provider>
}
