import React from 'react'
import TypeTable from '../dataTable/ProductTypeTable'
import ProductTypeAdd from '../components/ProductTypeAdd'
import { ProductTypeContext } from '../../context/ProductTypeCtx'
import { validation, validatorTypes } from '../../util/validator'
const ProductTypeContent = () => {
    const ctx = React.useContext(ProductTypeContext);
    const handleInputChange = (event) => {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        const formType = target.type;
        const validationTypes = {
            name: validatorTypes.POSITIVENUMBER,
            id: validatorTypes.POSITIVENUMBER,
        };
        // if (formType == "file") {
        //     if (event.target.files[0]) {
        //         Resizer.imageFileResizer(
        //             event.target.files[0],
        //             500,
        //             500,
        //             "png",
        //             100,
        //             0,
        //             (uri) => {
        //                 // console.log(uri.split(',')[1])
        //                 this.setState({ mainimage: uri });
        //             },
        //             "base64",
        //             200,
        //             200
        //         );
        //     }
        // }
        const validate = validation(
            value,
            validationTypes[name],
            name == "id" && ctx.type
        );

        console.log("value" + value);
        console.log("value" + ctx.errorMsg[name].show);

        let formValid = false;

        if (!validate.show) {
            let istemvavlid = true;
            Object.values(ctx.type).map((el, index) => {
                if (
                    el.length == 0 &&
                    Object.keys(ctx.type)[index] != [name]
                ) {
                    istemvavlid = false;
                    return;
                }
            });
            if (istemvavlid) {
                formValid = true;
            }
        }
        const errorMsg = {
            ...ctx.errorMsg,
            [name]: validate,
        }
        const type = {
            ...ctx.type,
            [name]: value,
        };

        ctx.setType(type);
        ctx.setErrorMsg(errorMsg);
        ctx.setFormValid(formValid);
    }

    return (
        <div className="content-wrapper">
            <div>
                <div className="content-header">
                    <div className="container-fluid">
                        <div className="row">
                            <div className="col-6">
                                <ProductTypeAdd formValid={ctx.formValid} errorMsg={ctx.errorMsg} type={ctx.type} handleInputChange={handleInputChange} addProductType={ctx.addProductType}></ProductTypeAdd>
                            </div>
                        </div>
                    </div>
                </div>

                <section className="content">
                    <div className="container-fluid">
                        <div className="row">
                            <div className="col-12"><TypeTable></TypeTable></div>
                        </div>
                    </div>
                </section>
            </div>
        </div >
    )
}

export default ProductTypeContent;
