import React, { useState } from 'react';
import firebase from '../../firebase'
const initialState = {
    types: [
        {
            id: "",
            name: ""
        }
    ],
    type: {
        id: "",
        name: ""
    },
    tableLoading: true,
    eventLoading: true,
    error: false
}
const productTypeCon = firebase.firestore().collection('productType');
export const ProductTypeContext = React.createContext();
export const ProductTypeProvider = (props) => {
    const [state, setState] = useState(initialState);
    React.useEffect(() => {
        getProductType();
    }, []);
    const getProductType = () => {
        productTypeCon.get().then((collections) => {
            const datas = collections.docs.map((type) => type.data());
            console.log("datas" + datas.length);
            setState({ loading: false, types: datas, error: false });
        }).catch((ex) => {
            console.log("error" + ex);
            setState({ loading: false, types: [{ "id": 1, "name": "umd" }], error: true });

        })
    }

    const addProductType = (type) => {
        setState({ type: type });
        databaseUpdate(type);
    }
    const databaseUpdate = (type) => {
        productTypeCon.doc(String(type.id)).set().then((res) => {
            alert("amjilttal nemlee");
        }).catch((ex) => console.log("aldaa garlaa") + ex).finally(ex => {
            getProductType();
        });
    }
    return <ProductTypeContext.Provider value={{ state, addProductType }}>{props.children}</ProductTypeContext.Provider>
}
