import React from 'react'

const Index = (props) => {
    return (
        props.updateAction ? <div className="text-right">
            <button onClick={props.add} disabled={props.formValid ? "" : "disabled"} type="submit" className="btn btn-primary">
                Өөрчлөлтийг хадгалах
      </button>
            <button onClick={props.cancelButtonAction} className="btn btn-light ml-2">
                Буцах
      </button>
        </div> : <div className="text-right">
            <button onClick={props.add} disabled={props.formValid ? "" : "disabled"} type="submit" className="btn btn-success">
                Хадгалах
          </button>
        </div>

    );
}

export default Index;
