import React from "react";
import css from "./style.module.css";
const Index = (props) => {
  return (
    <div>
      <div className="form-group">
        <label htmlFor="exampleInputId">Барааны линк:</label>
        <input
          name="link"
          id="exampleInputId"
          onChange={props.handleInputChange}
          type="text"
          value={props.product.link}
          className="form-control"
          placeholder="Барааны линк"
          autoComplete="off"
        />
        <p className={css.errorMsg}>
          {props.errorMsg.link.show && props.errorMsg.link.message}
        </p>
      </div>
      <div className="form-group">
        <label htmlFor="exampleInputPassword1">Барааны нэр:</label>
        <input
          name="name"
          value={props.product.name}
          type="name"
          className="form-control"
          placeholder="Барааны нэр"
          onChange={props.handleInputChange}
          autoComplete="off"
        />
        <p className={css.errorMsg}>
          {props.errorMsg.name.show && props.errorMsg.name.message}</p>
      </div>

      <div className="form-group">
        <label>Барааны төрөл:</label>
        <select
          name="type"
          className="form-control"
          value={props.product.typeId}
        >
          {/* <option value=""></option> */}
          {props.productTypes.map((el) => {
            return <option key={el.id} value={el.id}>{el.name}</option>
          })}
        </select>
        <p className={css.errorMsg}>
        </p>
      </div>

      <div className="form-group">
        <label>Барааны дэд төрөл:</label>
        <select
          name="subTypeId"
          className="form-control"
          value={props.product.subTypeId}
          onChange={props.handleInputChange}
        >
          <option value=""></option>
          {props.productSubTypes.map((el) => {
            return <option key={el.id} value={el.id}>{el.name}</option>
          })}
        </select>
        <p className={css.errorMsg}>
        </p>
      </div>
      <div className="form-group">
        <label>Барааны хэмжээсүүд:</label>
        {props.sizes.map((el, index) => {
          return <div className="row">
            <div className="form-group">
              <input
                name={index}
                type="text"
                value={props.sizes.value}
                onChange={props.handleInputSize}
                placeholder="үндсэн үнэ юань"
                className="form-control col-4"
                autoComplete="off"
                min="0"
              ></input>
              <p className={css.errorMsg}></p>
              <button className="btn btn-danger">remove</button>
            </div>
          </div>
        })}
        <button className="btn btn-success">нэмэх</button>

      </div>
    </div>
  );
};

export default Index;
