import { validation } from '../../util/validator'

const handleInputChange = (event, validationTypes, Resizer, setMainImage, model, errorMsgModel
    , setModel, setErrorMsg, setFormValid
) => {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    const type = target.type;

    if (type == 'file') {
        if (event.target.files[0]) {
            Resizer.imageFileResizer(
                event.target.files[0],
                500,
                500,
                'png',
                100,
                0,
                uri => {
                    setMainImage(uri);

                },
                'base64',
                200,
                200,
            );

        }
    }
    const validate = validation(
        value,
        validationTypes[name],
    );
    let formValid = false;
    const tempModel = {
        ...ctx.model,
        [name]: value,
    };
    if (!validate.show) {
        let istemvavlid = true;
        Object.values(model).map((el, index) => {
            if (
                el.length === 0 &&
                Object.keys(model)[index] !== [name]
            ) {
                istemvavlid = false;
                return;
            }
        });
        if (istemvavlid) {
            formValid = true;
        }
    }
    const errorMsg = {
        ...errorMsgModel,
        [name]: validate,
    }


    setModel(tempModel);
    setErrorMsg(errorMsg);
    setFormValid(formValid);
}

export default handleInputChange;
