import React from 'react'

const Index = (props) => {
    const index = props.index;
    const el = props.el;
    return <div className="row mt-1">
        <input
            name="value"
            type="text"
            value={el.value}
            onChange={(event) => { props.handleInputSize(event, index) }}
            placeholder={"Хэмжээс " + (index + 1)}
            className="form-control col-1"
            autoComplete="off"
            min="0"
        ></input>
        <button className={el.selected ? "btn btn-info ml-2" : "btn btn-warning ml-2"}
            onClick={() => { props.selectSize(index) }}
        >{el.selected ? "Сонгогдсон" : "Сонгогдоогүй"}</button>
        {el.selected && <div class="form-check">
            <input type="checkbox" class="form-check-input ml-0" onChange={() => { props.stateEvent(index) }} checked={el.state} id={index} />
            <label class="form-check-label ml-3" for={index}>Бэлэн байгаа</label>
        </div>}
        {el.state && <input
            name="quantity"
            type="text"
            value={el.quantity}
            onChange={(event) => { props.handleInputSize(event, index) }}
            placeholder="Тоо шир"
            className="form-control col-1 ml-1"
            autoComplete="off"
            min="0"
        ></input>}
        <button key={index} onClick={() => { props.removeSize(index) }} className="btn btn-danger ml-1">Хэмжээс хасах</button>
    </div>
}

export default Index;
