import React from "react";
import ProductAdd from "../components/ProductAdd";
import ProductControl from "../components/ProductControl";
import { ProductContext } from '../../context/ProductCtx'
import { validation, validatorTypes } from '../../util/validator'
const Proudct = () => {
  const ctx = React.useContext(ProductContext);
  const handleInputChange = (event) => {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    const validationTypes = {

    };
    const validate = validation(
      value,
      validationTypes[name],
      // name == "id" && ctx.types
    );
    let formValid = false;
    const product = {
      ...ctx.product,
      [name]: value,
    };
    if (!validate.show) {
      let istemvavlid = true;
      Object.values(product).map((el, index) => {
        if (
          el.length === 0 &&
          Object.keys(ctx.product)[index] !== [name]
        ) {
          istemvavlid = false;
          return;
        }
      });
      if (istemvavlid) {
        formValid = true;
      }
    }
    const errorMsg = {
      ...ctx.errorMsg,
      [name]: validate,
    }


    ctx.setProduct(product);
    ctx.setErrorMsg(errorMsg);
    ctx.setFormValid(formValid);
  }

  return (
    <div className="content-wrapper">
      <div>
        <div className="content-header">
          <div className="container-fluid">
            <div className="row mb-2">

              <div className="col-12">
                < ProductAdd product={ctx.product} errorMsg={ctx, errorMsg} handleInputChange={handleInputChange} ></ProductAdd>
                <ProductControl></ProductControl>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
export default Proudct;
