import React from 'react'
import TypeTable from '../dataTable/ProductTypeTable'
import ProductTypeAdd from '../components/ProductTypeAdd'
import { ProductTypeContext } from '../../context/ProductTypeCtx'
import { validation, validatorTypes } from '../../util/validator'
const ProductTypeContent = () => {
    const ctx = React.useContext(ProductTypeContext);
    console.log("ctx" + ctx.state)
    const handleInputChange = (event) => {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        const type = target.type;
        const validationTypes = {
            price: validatorTypes.POSITIVENUMBER,
            id: validatorTypes.EMAIL,
        };
        // if (type == "file") {
        //     if (event.target.files[0]) {
        //         Resizer.imageFileResizer(
        //             event.target.files[0],
        //             500,
        //             500,
        //             "png",
        //             100,
        //             0,
        //             (uri) => {
        //                 // console.log(uri.split(',')[1])
        //                 this.setState({ mainimage: uri });
        //             },
        //             "base64",
        //             200,
        //             200
        //         );
        //     }
        // }
        const validate = validation(
            value,
            validationTypes[name],
            name == "id" && ctx.state.type
        );

        let formValid = false;

        if (!validate.show) {
            let istemvavlid = true;
            Object.values(ctx.state.type).map((el, index) => {
                if (
                    el.length == 0 &&
                    Object.keys(ctx.state.type)[index] != [name]
                ) {
                    istemvavlid = false;
                    return;
                }
            });
            if (istemvavlid) {
                formValid = true;
            }
        }
        ctx.setState({
            errorMsg: {
                ...ctx.state.errorMsg,
                [name]: validate,
            },
            type: {
                ...ctx.state.type,
                [name]: value,
            },
            formValid: formValid,
        });
    }

    return (
        <div className="content-wrapper">
            <div>
                <div className="content-header">
                    <div className="container-fluid">
                        <div className="row">
                            <div className="col-6">
                                <ProductTypeAdd state={ctx.state} addProductType={ctx.addProductType}></ProductTypeAdd>
                            </div>
                        </div>
                    </div>
                </div>

                <section className="content">
                    <div className="container-fluid">
                        <div className="row">
                            <div className="col-12"><TypeTable></TypeTable></div>
                        </div>
                    </div>
                </section>
            </div>
        </div >
    )
}

export default ProductTypeContent;
