import React, { useState } from 'react';
import { productCon, productSubTypeCon, productTypeCon, productStorage } from '../context/CollectionsName/names'
import sizeDatas, { tempdata } from '../localdata/product_size'
export const ProductContext = React.createContext();
export const ProductProvider = (props) => {
    const productModel = {
        id: "1",
        link: "",
        name: "",
        sizes: [{
        }],
        image: "",
        price: "",
        salePrice: "0",
        typeId: "1",
        subTypeId: "",
    };

    const mainImageModel = "";
    const errorModel =
    {
        id: { show: false },
        link: { show: false },
        name: { show: false },
        image: { show: false },
        sizes: { show: false },
        price: { show: false },
        typeId: { show: false },
        subTypeId: { show: false },
    }


    const [mainImage, setMainImage] = React.useState(mainImageModel);
    const [sizes, setSizes] = useState(sizeDatas)
    const [product, setProduct] = useState(productModel);
    const [products, setProducts] = useState(null);
    const [productTypes, setProductTypes] = useState([]);
    const [productSubTypes, setProductSubTypes] = useState([]);
    const [updateAction, setUpdateAction] = useState(false);
    const [errorMsg, setErrorMsg] = useState(errorModel);
    const [event, setEvent] = useState({
        loading: true,
        error: false
    });

    const getProducts = async () => {
        productCon.get().then((collections) => {
            const datas = collections.docs.map((product) => product.data());
            setTableLoading(datas, false, false);
            console.log("datas" + datas.length);
        }).catch((ex) => {
            console.log("error" + ex);
            setTableLoading([], false, false);
        });

    }
    const getProductTypes = async () => {
        productTypeCon.get().then((collections) => {
            const datas = collections.docs.map((product) => product.data());
            setProductTypes(datas);
            getProductSubTypes();
            console.log("datas" + datas.length);
        }).catch((ex) => {
            console.log("error" + ex);
            setTableLoading([], false, false);
        });
    }

    const getProductSubTypes = async () => {
        productSubTypeCon.get().then((collections) => {
            const datas = collections.docs.map((product) => product.data());
            setProductSubTypes(datas);
            console.log("datasSub" + datas);
        }).catch((ex) => {
            console.log("error" + ex);

        });
    }

    React.useEffect(() => {
        getProductTypes();
    }, [])
    const [table, setTable] = useState({
        loading: true,
        error: false
    });
    const [formValid, setFormValid] = useState(false);


    const uploadImageFirebase = () => {
        const id = new Date().getTime();
        console.log("id", id);
        console.log("mainImage", mainImage);
        productStorage.ref(id.toString())
            .putString(mainImage.split(',')[1], `base64`, { contentType: `image/png` })
            .then(responce => {
                responce.ref.getDownloadURL().then(downloadURL => {

                    addProductData(downloadURL);
                });
            }).catch(ex => {
                setEventLoading(product, false, false);
                alert("Зураг нэмхэд алдаа гарлаа" + ex.toString());
            }).finally();
    }

    const addProduct = () => {
        product.sizes = sizes.filter(el => {
            return el.selected
        });
        setEventLoading(product, true, false);
        uploadImageFirebase();
        console.log("product", product);

    }

    const addProductData = (downloadURL) => {
        setProduct({
            ...product,
            image: downloadURL
        });
        productCon.doc().set(product).then((collections) => {
            console.log("collections" + collections);
            setUpdateAction(false);
            setEventLoading(product, false, false);
            setSizes(sizeDatas);
            setSizes(tempdata);
            setFormValid(false);
            alert(updateAction ? "Ажилттай заслаа" : "Ажилттай нэмлээ");

        }).catch((ex) => {
            console.log("error" + ex);
            setEventLoading(product, false, false);
            alert(updateAction ? "Засхад алдаа гарлаа " : "Нэмхэд алдаа гарлаа ");
        });
    }

    const setEventLoading = (product, isLoading, error) => {
        event.loading = isLoading;
        event.error = error;
        setEvent(event);
        setProduct(productModel);
        setFormValid(false);
    }

    const setTableLoading = (products, isLoading, error) => {
        table.loading = isLoading;
        table.error = error;
        setTable(table);
        setProducts(products);
    }



    return <ProductContext.Provider value={{ mainImage, setMainImage, sizes, setSizes, errorModel, productModel, updateAction, products, table, product, productTypes, productSubTypes, errorMsg, formValid, event, setErrorMsg, setProduct, setFormValid, addProduct, setUpdateAction, getProducts }}>{props.children}</ProductContext.Provider>
}
